<script>
    // Toggle the dropdown menu
    document.getElementById("dropdownButton").addEventListener("click", function() {
        const dropdownButton = document.getElementById("dropdownButton");
        const dropdownList = document.getElementById("dropdownList");
        // Get button's position and dimensions
        const rect = dropdownButton.getBoundingClientRect();

        // Position the dropdown just below the button
        dropdownList.style.top = `${rect.bottom + window.scrollY}px`;
        dropdownList.style.left = `${rect.left + window.scrollX}px`;

        dropdownList.classList.toggle("hidden");
    });

    // Update selected option and close the menu
    document.querySelectorAll("#dropdownList li").forEach(function(item) {
        item.addEventListener("click", function() {
            // Update the button's label with the selected option
            document.getElementById("selectedOption").textContent = item.textContent;

            // Remove 'selected' class from all options and add it to the clicked one
            document.querySelectorAll("#dropdownList li").forEach(li => li.classList.remove("selected"));
            item.classList.add("selected");

            // Close the dropdown
            document.getElementById("dropdownList").classList.add("hidden");
        });
    });

    document.addEventListener("click", function(event) {
        const dropdownList = document.getElementById("dropdownList");
        const dropdownButton = document.getElementById("dropdownButton");

        // If the click is outside the dropdown or the button, close the dropdown
        if (!dropdownList.contains(event.target) && !dropdownButton.contains(event.target)) {
            dropdownList.classList.add("hidden");
        }
    });

    document.getElementById("filter-customize-form").addEventListener("submit", function(event) {
        event.preventDefault();

        const form = this;
        const formData = {};
        
        Array.from(form.elements).forEach(function(element) {
            if (element.name) {
                formData[element.name] = element.value;
            }
        });

        console.log({formData});
        
        window.location.href = "/gainers-losers";
    });
</script>
