<script>
    // Toggle the dropdown menu
    document.getElementById("coin-moreButton").addEventListener("click", function() {
        const dropdownButton = document.getElementById("coin-moreButton");
        const dropdownList = document.getElementById("coin-dropdownList");

        // Get button's position and dimensions
        const rect = dropdownButton.getBoundingClientRect();

        // Position the dropdown just below the button
        dropdownList.style.top = `${rect.bottom + window.scrollY + 8}px`;
        dropdownList.style.left = `${rect.left + window.scrollX - rect.width - rect.width}px`;

        dropdownList.classList.toggle("hidden");
    });

    // Update selected option and close the menu
    document.querySelectorAll("#coin-dropdownList li").forEach(function(item) {
        item.addEventListener("click", function() {

            // Remove 'selected' class from all options and add it to the clicked one
            document.querySelectorAll("#coin-dropdownList li").forEach(li => {
                li.classList.remove("selected");
                li.classList.remove("underline");
            });
            item.classList.add("selected");
            item.classList.add("underline");

            // Close the dropdown
            document.getElementById("coin-dropdownList").classList.add("hidden");
        });
    });

    document.addEventListener("click", function(event) {
        const dropdownList = document.getElementById("coin-dropdownList");
        const dropdownButton = document.getElementById("coin-moreButton");

        // If the click is outside the dropdown or the button, close the dropdown
        if (!dropdownList.contains(event.target) && !dropdownButton.contains(event.target)) {
            dropdownList.classList.add("hidden");
        }
    });
</script>